import React, { useEffect, useState } from "react";
import ChatUI from "../components/ChatUI";
import NutritionDashboard from "../components/NutritionDashboard";

const Home = () => {
  const [profiles, setProfiles] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState(null);
  const [nutrition, setNutrition] = useState(null);
  const [mealPlan, setMealPlan] = useState([]);
  const [groceryList, setGroceryList] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/v1/profiles")
      .then((res) => res.json())
      .then((data) => setProfiles(data.profiles || []));
  }, []);

  const handleProfileSelect = async (profile) => {
    setSelectedProfile(profile);
    // Fetch personalized meal plan
    const res = await fetch("http://127.0.0.1:8000/v1/plan/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(profile),
    });
    const data = await res.json();
    setNutrition(data.nutrition);
    setMealPlan(data.meal_plan);
    setGroceryList(data.grocery_list);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-200 via-blue-100 to-blue-300 flex flex-col items-center justify-center py-10">
      <div className="w-full max-w-3xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-extrabold mb-4 text-center text-blue-700 drop-shadow-lg">
            Nutrition Chatbot
          </h1>
          <div className="flex flex-wrap gap-3 justify-center">
            {profiles.length === 0 && (
              <span className="text-gray-500">No profiles saved yet.</span>
            )}
            {profiles.map((profile, idx) => (
              <button
                key={idx}
                className={`px-6 py-3 rounded-xl shadow-lg font-semibold transition-all duration-200 border-2 ${
                  selectedProfile === profile
                    ? "bg-gradient-to-r from-blue-500 to-green-400 text-white border-blue-400 scale-105"
                    : "bg-white text-blue-500 border-blue-200 hover:bg-blue-50 hover:scale-105"
                }`}
                onClick={() => handleProfileSelect(profile)}
              >
                {profile.name || `Profile ${idx + 1}`}
              </button>
            ))}
          </div>
        </div>
        <div className="rounded-2xl shadow-2xl bg-white/80 p-6 mb-8">
          <ChatUI />
        </div>
        {nutrition && (
          <NutritionDashboard
            calories={nutrition.calories}
            macros={Object.entries(nutrition.macros).map(([name, value]) => ({
              name,
              value,
            }))}
            mealPlan={mealPlan.map(
              (meal) => `${meal.meal}: ${meal.items.join(", ")}`
            )}
            groceryList={groceryList}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
