import React, { useEffect, useState } from "react";
import ChatUI from "../components/ChatUI";
import NutritionDashboard from "../components/NutritionDashboard";

const Home = () => {
  const [profiles, setProfiles] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState(null);
  const [nutrition, setNutrition] = useState(null);
  const [mealPlan, setMealPlan] = useState([]);
  const [groceryList, setGroceryList] = useState([]);
  const [showProfileForm, setShowProfileForm] = useState(false);
  const [profileForm, setProfileForm] = useState({
    name: "",
    age: "",
    gender: "",
    height: "",
    weight: "",
    activity: "",
    goal: "",
    allergies: "",
    preferences: "",
    diet_category: ""
  });

  useEffect(() => {
    fetch("http://127.0.0.1:8000/v1/profiles")
      .then((res) => res.json())
      .then((data) => setProfiles(data.profiles || []));
  }, [showProfileForm]);

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleProfileFormSubmit = async (e) => {
    e.preventDefault();
    await fetch("http://127.0.0.1:8000/v1/profiles/save", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(profileForm),
    });
    setShowProfileForm(false);
    setProfileForm({
      name: "",
      age: "",
      gender: "",
      height: "",
      weight: "",
      activity: "",
      goal: "",
      allergies: "",
      preferences: "",
      diet_category: ""
    });
    // Refresh profiles
    fetch("http://127.0.0.1:8000/v1/profiles")
      .then((res) => res.json())
      .then((data) => setProfiles(data.profiles || []));
  };

  const handleProfileSelect = async (profile) => {
    setSelectedProfile(profile);
    // Fetch personalized meal plan
    const res = await fetch("http://127.0.0.1:8000/v1/plan/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(profile),
    });
    const data = await res.json();
    setNutrition(data.nutrition);
    setMealPlan(data.meal_plan);
    setGroceryList(data.grocery_list);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-200 via-blue-100 to-blue-300 flex flex-col items-center justify-center py-10">
      <div className="w-full max-w-3xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-extrabold mb-4 text-center text-blue-700 drop-shadow-lg">
            Nutrition Chatbot
          </h1>
          <div className="flex flex-wrap gap-3 justify-center mb-4">
            <button
              className="px-6 py-3 rounded-xl shadow-lg font-semibold bg-gradient-to-r from-green-400 to-blue-500 text-white border-2 border-blue-400 hover:scale-105 transition-all duration-200"
              onClick={() => setShowProfileForm(true)}
            >
              + Add New Profile
            </button>
            {profiles.length === 0 && (
              <span className="text-gray-500">No profiles saved yet.</span>
            )}
            {profiles.map((profile, idx) => (
              <button
                key={idx}
                className={`px-6 py-3 rounded-xl shadow-lg font-semibold transition-all duration-200 border-2 ${
                  selectedProfile === profile
                    ? "bg-gradient-to-r from-blue-500 to-green-400 text-white border-blue-400 scale-105"
                    : "bg-white text-blue-500 border-blue-200 hover:bg-blue-50 hover:scale-105"
                }`}
                onClick={() => handleProfileSelect(profile)}
              >
                {profile.name || `Profile ${idx + 1}`}
              </button>
            ))}
          </div>
          {showProfileForm && (
            <form className="bg-white rounded-xl shadow-lg p-6 mt-4 max-w-xl mx-auto" onSubmit={handleProfileFormSubmit}>
              <h2 className="text-2xl font-bold mb-4 text-blue-700">Create New Profile</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <input name="name" value={profileForm.name} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Name" required />
                <input name="age" value={profileForm.age} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Age" required />
                <select name="gender" value={profileForm.gender} onChange={handleProfileFormChange} className="border rounded px-3 py-2" required>
                  <option value="">Gender</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </select>
                <input name="height" value={profileForm.height} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Height (cm)" required />
                <input name="weight" value={profileForm.weight} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Weight (kg)" required />
                <select name="activity" value={profileForm.activity} onChange={handleProfileFormChange} className="border rounded px-3 py-2" required>
                  <option value="">Activity Level</option>
                  <option value="sedentary">Sedentary</option>
                  <option value="light">Light</option>
                  <option value="moderate">Moderate</option>
                  <option value="active">Active</option>
                  <option value="very active">Very Active</option>
                </select>
                <select name="goal" value={profileForm.goal} onChange={handleProfileFormChange} className="border rounded px-3 py-2" required>
                  <option value="">Goal</option>
                  <option value="loss">Weight Loss</option>
                  <option value="gain">Weight Gain</option>
                  <option value="maintenance">Maintenance</option>
                </select>
                <input name="allergies" value={profileForm.allergies} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Allergies (comma separated)" />
                <input name="preferences" value={profileForm.preferences} onChange={handleProfileFormChange} className="border rounded px-3 py-2" placeholder="Diet Preferences (e.g. vegan, halal)" />
                <select name="diet_category" value={profileForm.diet_category} onChange={handleProfileFormChange} className="border rounded px-3 py-2">
                  <option value="">Diet Category</option>
                  <option value="halal">Halal</option>
                  <option value="vegan">Vegan</option>
                  <option value="keto">Keto</option>
                  <option value="diabetic">Diabetic-friendly</option>
                  <option value="gluten_free">Gluten-free</option>
                </select>
              </div>
              <div className="flex gap-4 mt-6">
                <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded font-bold shadow hover:bg-blue-600">Save Profile</button>
                <button type="button" className="bg-gray-300 text-gray-700 px-6 py-2 rounded font-bold shadow hover:bg-gray-400" onClick={() => setShowProfileForm(false)}>Cancel</button>
              </div>
            </form>
          )}
        </div>
        <div className="rounded-2xl shadow-2xl bg-white/80 p-6 mb-8">
          <ChatUI />
        </div>
        {nutrition && (
          <NutritionDashboard
            calories={nutrition.calories}
            macros={Object.entries(nutrition.macros).map(([name, value]) => ({
              name,
              value,
            }))}
            mealPlan={mealPlan.map(
              (meal) => `${meal.meal}: ${meal.items.join(", ")}`
            )}
            groceryList={groceryList}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
