import React, { useState } from "react";
import ChatUI from "../components/ChatUI";
import NutritionDashboard from "../components/NutritionDashboard";

const Home = () => {
  const [nutrition, setNutrition] = useState(null);
  const [mealPlan, setMealPlan] = useState([]);
  const [groceryList, setGroceryList] = useState([]);

  // Profile features removed

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-200 via-blue-100 to-blue-300 flex flex-col items-center justify-center py-10">
      <div className="w-full max-w-3xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-extrabold mb-4 text-center text-blue-700 drop-shadow-lg">
            Nutrition Chatbot
          </h1>
        </div>
        <div className="rounded-2xl shadow-2xl bg-white/80 p-6 mb-8">
          <ChatUI />
        </div>
        {nutrition && (
          <NutritionDashboard
            calories={nutrition.calories}
            macros={Object.entries(nutrition.macros).map(([name, value]) => ({
              name,
              value,
            }))}
            mealPlan={mealPlan.map(
              (meal) => `${meal.meal}: ${meal.items.join(", ")}`
            )}
            groceryList={groceryList}
          />
        )}
      </div>
    </div>
  );
};

export default Home;
