import React, { useState, useRef, useEffect } from "react";

const ChatUI = () => {
  const [messages, setMessages] = useState([
    {
      sender: "bot",
      text: "Hi ðŸ‘‹ Iâ€™m your personal nutrition coach! Can I ask a few questions to personalize your plan?",
    },
  ]);
  const [input, setInput] = useState("");
  const chatWindowRef = useRef(null);

  useEffect(() => {
    // Scroll to bottom when messages update
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    const userMsg = { sender: "user", text: input };
    setMessages((msgs) => [...msgs, userMsg]);
    setInput("");

    // Call backend API (replace URL if needed)
    try {
      const res = await fetch("http://127.0.0.1:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });
      const data = await res.json();
      setMessages((msgs) => [
        ...msgs,
        { sender: "bot", text: data.reply || "Sorry, I didn't get that." },
      ]);
    } catch (err) {
      setMessages((msgs) => [
        ...msgs,
        { sender: "bot", text: "Error connecting to server." },
      ]);
    }
  };

  return (
    <div className="flex flex-col h-full w-full bg-white rounded-lg shadow-lg p-4">
      {/* Chat bubbles and typing effect will go here */}
      <div
        className="flex-1 overflow-y-auto mb-4"
        id="chat-window"
        ref={chatWindowRef}
      >
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`flex mb-2 ${msg.sender === "user" ? "justify-end" : "justify-start"}`}
          >
            <div
              className={`px-4 py-2 rounded-lg max-w-xs whitespace-pre-line break-words shadow text-sm ${
                msg.sender === "user"
                  ? "bg-blue-500 text-white rounded-br-none"
                  : "bg-gray-200 text-gray-900 rounded-bl-none"
              }`}
            >
              {msg.text}
            </div>
          </div>
        ))}
      </div>
      <form className="flex items-center gap-2" onSubmit={handleSend}>
        <input
          type="text"
          className="flex-1 border rounded px-3 py-2 focus:outline-none focus:ring"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatUI;
